import React from "react";
import { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from "bootstrap";
function FormValidation(){
  const [values,setValues]=useState({
    firstName:'',
    email:'',
    password:'',
  })
  function handleInput(event){
    const newObj={...values,[event.target.name]:event.target.values}
    setValues(newObj)
  }
  return(
    <div className="signup_container w-100 d-flex justify-content-center">
      <div className="signup_form mt-5 w-50">
        <h4>Form Validate</h4>
        <div className="form mt-3">
          <form className="border p-3" onSubmit={handleValidation}>
            <label for="name" className="mt-2"><strong>FirstName</strong></label>
            <input type="text" placeholder="Enter name" name="name" className="form-control" onChange={{handleInput}}/>
            <label for="email" className="mt-2"><strong>Email</strong></label>
            <input type="email" placeholder="Enter Email" name="email" className="form-control" onChange={{handleInput}}/>
            <label for="password" className="mt-2"><strong>Password</strong></label>
            <input type="password" placeholder="Enter Password" name="password" className="form-control" onChange={{handleInput}}/>
            <div className="mt-3">
              <button className="btn btn-sucess w-50">Validate</button>
            </div>
          </form>

        </div>
      </div>  
    </div>
  );
}

export default FormValidation;
