import { FormControl, Grid ,Typography} from "@mui/material";
import { Form, Formik } from "formik";
import React from "react";
function EditUser(){
  return(
    <div>
      <div>
      <h1 align="center" style={{ marginBottom: 25, marginTop: 50 }}>
            Edit User
          </h1>

          <center>
            <hr
              style={{
                background: "red",
                color: "red",
                borderColor: "red",
                height: "4px",
                marginInline: "30px",
                width: "200px",
              }}
            />
          </center>
          <Formik
          // initialValues={initialValueState}
          // validationSchema={validationSchema}
          // enableReinitialize={true}
          // onSubmit={onSubmit}
          >
            {({
              values,touched,errors,onChange
            })=>(
              <form>
                <Grid direction="column">
                  <Grid direction="row">
                    <FormControl>
                    <Typography variant="body1" gutterBottom >
              Email *
            </Typography>
            <TextField
              style={{
                height: 50,
                width: 600,
                // marginInline: 30,
                // marginTop: 20,
              }}
              fullWidth
              id="email"
              name="email"
              type="email"
              value={ values.email}
              onChange={ handleChange}
              error={ touched.email && Boolean( errors.email)}
              helperText={ touched.email &&  errors.email}
            />
                    </FormControl>
                    <FormControl>
                    <Typography variant="body1" gutterBottom >
              Email *
            </Typography>
            <TextField
              style={{
                height: 50,
                width: 600,
                // marginInline: 30,
                // marginTop: 20,
              }}
              fullWidth
              id="email"
              name="email"
              type="email"
              value={values.email}
              onChange={handleChange}
              error={ touched.email && Boolean( errors.email)}
              helperText={ touched.email &&  errors.email}
            />
                    </FormControl>
                  </Grid>
                </Grid>
              </form>
            )
            }

          </Formik>
      </div>
    </div>
  );
}

export default EditUser;
