import ValidationErrorMessage from "../component/ValidateErrorMessage";
import * as Yup from "yup";
const { Breadcrumbs, Typography, TextField, FormControl, InputLabel, MenuItem } = require("@mui/material");
const { getRoles } = require("@testing-library/react");
const { Button } = require("bootstrap");
const { Formik } = require("formik");
const { useState, useEffect } = require("react");
const { Link } = require("react-router-dom");

const Register=()=>{
 // const classes=createAccountStyle();
  //const navigate=useNavigate();
  const initialValues={
    firstName:"",
    LastName:"",
    email:"",
    roleId:0,
    password:"",
    confirmPassword:"",
  };
  const [roleList,setRoleList]=useState([]);
useEffect(()=>{
  if(roleList.length) return;
  getRoles();
},[roleList]);
const getRoles=()=>{
  userService.getAllRoles().then((res)=>{
    setRoleList(res);
  });
};
const validationSchema= Yup.object().shape({
  email:Yup.string().email("Invalid email address formate")
  .require("Email is required"),
  password:Yup.string().min(6,"Password must be 5 character at minimum")
  .require("Password is required"),
  confirmPassword:Yup.string().oneOf([Yup.ref("password"),null],"Password and Confirm Password must be match.")
  .require("Confirm Password is required"),
  firstName:Yup.string().required("First name is required"),
  lastName:Yup.string().required("last name is required"),
  roleId:Yup.number().required("Role is required"),
});

const onSubmit=(data)=>{
  alert(JSON.stringify(data))
  delete data.confirmPassword;
};
return(
  
  // <div className={classes.createAccountWrapper}>
  <div>
    <div className="create-account-page -wrapper">
      <div className="container">
        <Breadcrumbs separator=">"
        aria-label="breadcrump"
        className="breadcrump-wrapper">
          <Link color="inherit" href="/" title="Home">Home</Link>
          <Typography color="textPrimary">Create an Account</Typography>
        </Breadcrumbs>
        <Typography variant="h1">Login or Create an Account</Typography>
        <div className="create-account-row">
          <Formik initialValues={initialValues}
          validationSchema={validationSchema}
          onSubmit={onSubmit}
          >
            {({
              values,
              errors,
              touched,
              handleBlur,
              handleChange,
              handleSubmit,
              initialValues
            })=>(
              <form onSubmit={handleSubmit}>
                <div className="form-block">
                  <div className="personal-information">
                    <Typography variant="h2">Personal Information</Typography>
                    <p>Please enter the following information to create your account.</p>
                    <div className="form-row-wrapper">
                      <div className="form-col">
                        <TextField id="first-name"
                        defaultValue={initialValues.firstName}
                        name="firstName"
                        label="First Name *"
                        inputProps={{className:"small"}}
                        onBlur={handleBlur}
                        variant="outlined"
                        onChange={handleChange} />
                        <validationErrorMessage message={errors.firstName}
                        touched={touched.firstName} />
                      </div>
                      <div className="form-col">
                        <TextField id="last-name"
                        defaultValue={initialValues.LastName}
                        name="lastName"
                        label="Last Name *"
                        inputProps={{className:"small"}}
                        onBlur={handleBlur}
                        variant="outlined"
                        onChange={handleChange} />
                        <validationErrorMessage message={errors.lastName}
                        touched={touched.lastName} />
                      </div>
                      <div className="form-col">
                        <TextField id="email"
                        defaultValue={initialValues.email}
                        variant="outlined"
                        name="email"
                        label="email Address *"
                        inputProps={{className:"small"}}
                        onBlur={handleBlur}
                        onChange={handleChange} />
                        <validationErrorMessage message={errors.email}
                        touched={touched.email} />
                      </div>
                      <div className="form-col">
                        <FormControl
                        className="dropdown-wrapper"
                        variant="outlined">
                          <InputLabel htmlFor="select">Roles</InputLabel>
                          <select name="roleId"
                          id={"roleId"}
                          defaultValue={initialValues.roleId}
                          inputProps={{className:"small"}}
                          onChange={handleChange}
                          //className={materialClasses.customSelect}
                          // MenuProps={{
                          //   classes:{
                          //     paper:materialClasses.customSelect,
                          //   },
                          // }}
                          value={values.roleId}
                          >
                            {roleList.length>0 && roleList.map((role)=>(
                              <MenuItem
                              value={role.id}
                              key={"name"+role.id}
                              >{role.name}</MenuItem>
                            ))}
                          </select>
                          <validationErrorMessage
                          message={errors.roleId}
                          touched={touched.roleId}
                          />
                        </FormControl>

                      </div>

                    </div>
                  </div>
                </div>
                <div className="login-information">
                  <Typography variant="h2">Login Information</Typography>
                              <div className="form-row-wrapper">
                              <div className="form-col">
                        <TextField id="password"
                        type="password"
                        variant="outlined"
                        name="password"
                        label="Password *"
                        inputProps={{className:"small"}}
                        onBlur={handleBlur}
                        onChange={handleChange} />
                        <validationErrorMessage message={errors.password}
                        touched={touched.password} />
                      </div>
                      <div className="form-col">
                        <TextField id="confirm-password"
                        type="password"
                        
                        variant="outlined"
                        name="confirm-password"
                        label="Confirm Password *"
                        inputProps={{className:"small"}}
                        onBlur={handleBlur}
                        onChange={handleChange} />
                        <validationErrorMessage message={errors.confirmPassword}
                        touched={touched.confirmPassword} />
                      </div>
                              </div>
                              <div className="btn-wrapper">
                                <Button className="pink-btn btn"
                                variant="contained"
                                type="submit"
                                color="primary"
                                disableElevation
                                >Register</Button>
                              </div>
                </div>
              </form>
            )}


          </Formik>

        </div>
      </div>
    </div>

  </div>
);
            };
            export default Register;
