import React from "react";
import Header from '../component/Header';
import Footer from '../component/Footer';
import { Button, FormControl, InputLabel, MenuItem, Pagination, Paper, Select, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, TextField, Typography } from "@mui/material";





function ProductList() {
    return (
        <div>
            <div>
                <Typography variant="h1">Book Listing</Typography>
                <Grid container>
                    <Grid item xs={6}>
                        <Typography variant="h2">
                            Total
                            <span> - {bookResponse.totalItems} items</span>
                        </Typography>
                    </Grid>
                    <div>
                        <TextField
                        id="text"
                        name="text"
                        placeholder="Search"
                        variant="outlined"
                        inputProps={{className:"small"}}
                        onChange={(e)=>{
                            setFilters({
                                ...filters,
                                keyword:e.target.value,
                                pageIndex:1,
                            });
                        }} />
                        </div>
                        <FormControl variant="outlined">
                            <InputLabel htmlFor="select">Sort By</InputLabel>
                            <Select 
                            className={materialClasses.customSelect}
                            MenuProps={{
                                classes:{paper:materialClasses.customSelect},
                            }}
                            onChange={sortBooks}
                            value={sortBy}>
                                <MenuItem value="a-z">a-z</MenuItem>
                                <MenuItem value="z-a">z-a</MenuItem>
                            </Select>
                            
                        </FormControl>
                    
                </Grid>
                <div>
                    <div>
                        {bookService.map((book,index)=>(
                            <div key={index}>
                                <div>
                                    <em>
                                        <img src={book.base64image}
                                        alt="dummyimage" />
                                    </em>
                                    <div>
                                        <Typography variant="h3">{book.name}</Typography>
                                        <span >{book.category}</span>
                                        <p>{book.description}</p>
                                        <p >
                                            <span>
                                                MRP&#8377;{book.price}
                                            </span>
                                        </p>
                                        <button >
                                            <span onClick={()=>addToCart(book)}>
                                                ADD TO CART
                                            </span>
                                            <span></span>
                                        </button>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
                <div>
                    <Pagination count={bookResponse.totalPages}
                    page={filters.pageIndex}
                    onChange={(e,newPage)=>{
                        setFilters({...filters,pageIndex:newPage});
                    }}/>

                </div>
            </div>
        </div>
    );
}

export default ProductList;
