import { useContext, useState } from "react";
import { useEffect } from "react";
import { createContext } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import shared from "../utils/shared";

const intialUserValue={
    id:0,
    email:"",
    firstName:"",
    lastName:"",
    roleId:"",
    role:"",
    password:"",
};
const initialState={
    setUser:()=>{},
   user:intialUserValue,
    signOut:()=>{},
    appInitialize:false,
}
export const AuthContext=createContext(initialState);

export const AuthWrapper=({children})=>{
    const [appInitialize,setAppInitialize]=useState(false);
    const[user,_setUser]=useState(intialUserValue);
    const navigate=useNavigate();
    const {pathname}=useLocation();

    const setUser=(user)=>{
        console.log("abc@wayne2.com",'user');
        localStorage.setItem('user',JSON.stringify(user));

        _setUser(user)
    };
    useEffect(()=>{
        const str=JSON.parse(localStorage.getItem('user'))|| intialUserValue;
        if(str.id){
            _setUser(str);
        }
        if(!str.id){
            navigate('/login');
        }
    },[]);
    const signOut=()=>{
        setUser(intialUserValue);
        localStorage.removeItem('user');
        navigate("/login");
    };

    useEffect(()=>{
        if(pathname==='./login' && user.id){
            navigate("/productList");
        }
        if(!user.id){
            return;
        }
        // const access=shared.hasAccess(pathname,user);
        // if(!access){
        //     toast.warning("Sorry,you are not authorized to access this page");
        //     navigate("/productList");
        //     return;
        // }
        setAppInitialize(true);
    },[pathname,user]);

    let value={
        user,
        setUser,
        signOut,
        appInitialize,
    };

    return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

export const useAuthContext=()=>{
    return useContext(AuthContext);
};
