import  React, { useState } from 'react';
import {TextField ,Button, ListItem }from '@mui/material';
import bookService from '../service/book.service';
import './Header.css';
import {List} from '@mui/material';
export default function FreeSolo() {
  const [value,setValue]=useState('');
  const [bookList,setbookList]=useState([]);
  const [openSearchResult,setOpenSearchResult]=useState(false);
  const onChange=(e)=>{
    setValue(e.target.value);
  };
  const searchBook =async()=>{
    const res =await bookService.searchBook(value);
    setbookList(res);
  };
  const search=()=>{
    document.body.classList.add("search-results-open");
    searchBook();
    setOpenSearchResult(true);
  };
    return (
     <div className="h-searchbar">
     <div className="h-searchbar-center">
         {/* <Autocomplete
             className="h-autocomplete"
             disablePortal
             id="combo-box-demo"
             options={topBooks}
             sx={{ width: 300 }}
             renderInput={(params) => <TextField {...params} placeholder="What are you looking for..."/>}
         /> */}
         <div className="h-txtfield">
             <TextField 
                 id="outlined-basic" 
                 placeholder='what are you looking for...' 
                 variant="outlined" 

                 name="text"
                 value={value}
                 onChange={onChange}
             />
             {openSearchResult && 
             <>
                 <div className="h-product-list">
                     <div className="h-no-prod">
                         {bookList?.length === 0 && (
                                 <p className="h-not-found"> No Products Found </p>
                             )
                         }
                     </div>
                     <List className="h-related-product-list">
                         {bookList?.length > 0 && bookList.map((item, i) => {
                             return(
                                 <ListItem>
                                     <div className="h-product-list-inner">
                                         <div className="h-inner-lft">
                                             <span className="txt-41 txt-lb">
                                                 {item.name}
                                             </span>
                                             <p>
                                                 {item.description}
                                             </p>
                                         </div>
                                         <div className="h-inner-rght">
                                             <span>
                                                 {item.price}
                                             </span>
                                             <Button
                                                 size="small"
                                                 className="c-f14d54"
                                             >
                                                 Add to Cart
                                             </Button>
                                         </div>
                                     </div>
                                 </ListItem>
                             )})    
                         }
                     </List>
                 </div>
             </>
             }
         </div>
    </div>
    </div>
      
    );
  }
