import React, { useState, useEffect } from "react";
import userService from "../service/user.service";
import { useNavigate } from "react-router-dom";
import { useAuthContext } from "../context/auth";
import { useParams } from "react-router-dom";
import * as Yup from "yup";
import { toast } from "react-toastify";
import { Form, Formik } from "formik";
import { FormControl, Grid, TextField, Typography } from "@mui/material";

function EditUser() {
  useEffect(() => {
    if (id) {
      getUserById();
      
    }
  }, [id]);
  const [roles, setRoles] = useState([]);
  const [user, setUser] = useState();
  const navigate = useNavigate();
  const initialValues = {
    id: 0,
    email: "",
    lastName: "",
    firstName: "",
    roleId: 3,
  };
  useEffect(() => {
    if (user && roles.length) {
      const roleId = roles.find((role) => role.name === user?.role)?.id;
      setInitialValueState({
        id: user.id,
        email: user.email,
        lastName: user.lastName,
        firstName: user.firstName,
        roleId,
        password: user.password,
      });
    }
  }, [user, roles]);
  const [initialValueState, setInitialValueState] = useState(initialValues);
  const { id } = useParams();
  useEffect(() => {
    getRoles();
  }, []);
  
  const getRoles = () => {
    userService.getAllRoles().then((res) => {
      if (res) {
        setRoles(res);
      }
    });
  };
  const getUserById = () => {
    userService.getById(Number(id)).then((res) => {
      if (res) {
        setUser(res);
        console.log("user",res);
      }
    });
  };
  return (
    <>
      <div>
        <div>
        <h1 align="center" style={{ marginBottom: 25, marginTop: 50 }}>
            Edit User
          </h1>
          <center>
            <hr
              style={{
                background: "red",
                color: "red",
                borderColor: "red",
                height: "4px",
                marginInline: "30px",
                width: "200px",
              }}
            />
          </center>
          <Formik>
            {({
              errors,
              touched,
              handleBlur,
              handleChange,
              handleSubmit,
            })=>
            (
              <Form onSubmit={handleSubmit}>
                <Grid direction="column">
                  <Grid direction="row">
                    <FormControl>
                    <Typography style={{ marginInline: 30,
                      marginTop: 50,}}>First Name*</Typography>
                    <TextField 
                    style={{
                      height: 50,
                      width: 550,
                      marginInline: 30,
                      
                    }}
                    value={user.firstName}
                      onBlur={handleBlur}
                      onChange={handleChange}
                      error={touched.firstName && Boolean(errors.firstName)}
                      helperText={touched.firstName && errors.firstName}
                    />
                    </FormControl>
                    <FormControl>
                    <Typography style={{
                      marginTop: 50,}}>Last Name*</Typography>
                    <TextField
                    style={{
                      height: 50,
                      width: 550,
                      // marginInline: 30,
                      // marginTop: 20,
                    }} 
                    value={user.lastName}
                    onBlur={handleBlur}
                    onChange={handleChange}
                    error={touched.firstName && Boolean(errors.firstName)}
                    helperText={touched.firstName && errors.firstName}/>
</FormControl>
                   
                  </Grid>
                  <Grid direction="row">

                  </Grid>
                </Grid>
              </Form>
            )}
          </Formik>
        </div>
      </div>
    </>
  );
}
export default EditUser;
