import  React, { useState } from 'react';
import {TextField ,Button }from '@mui/material';
import bookService from '../service/book.service';
import './Header.css';
export default function FreeSolo() {
  const [value,setValue]=useState('');
  const [bookList,setbookList]=useState([]);
  const [openSearchResult,setOpenSearchResult]=useState(false);
  const onChange=(e)=>{
    setValue(e.target.value);
  };
  const searchBook =async()=>{
    const res =await bookService.searchBook(value);
    setbookList(res);
  };
  const search=()=>{
    document.body.classList.add("search-results-open");
    searchBook();
    setOpenSearchResult(true);
  };
    return (
     <><TextField onChange={onChange} label="What are you looking for..." variant="outlined" size="small" style={{ marginRight: "10px", marginTop: "20px", width: "422px" }} />
     {openSearchResult && (
      <>
      <div className='search-overlay' onClick={()=>{
        setOpenSearchResult(false);
        document.body.classList.remove("search-results-open");
      }}>

      </div>
      <div className='product-listing'>
        {bookList?.length===0 && (
          <p >No product Found</p>
        )}
        <list>
          {bookList?.length>0 &&
          bookList.map((item,i)=>{
            return(
              <listItem key={i}>
                <div>
                  <div>
                    <span>{item.name}</span>
                    <p>{item.description}</p>
                  </div>
                  <div>
                    <span>
                      {item.price}
                    </span>
                    {/* onClick={()=>addToCart(item)} */}
                    <link >
                      Add to Cart
                    </link>
                  </div>
                </div>
              </listItem>
            );
          })}
        </list>
      </div>
</>
     )}
     <Button variant="contained" color="success" style={{ marginInline: "10px", marginTop: "20px", height: "40px" }} onClick={search}>
        Success
      </Button>
      </>
      
    );
  }
