import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import './Header.css';
import { Button, ButtonGroup, List, ListItem, TextField } from '@mui/material';
// import { addIcon } from "../assets";
// import { Autocomplete, TextField } from "@mui/material";
import { Search } from "@mui/icons-material";
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';

/* st api for search */
import bookService from "../service/book.service";
/* st api for search */



function Header() {

    const navigate = useNavigate();

    const handleLogin = () => {
        navigate('/login')
    };
    const handleRegister = () => {
        navigate('/register')
    };
    const handleCansal = () => {
        setOpenSearchResult(false)
    }

    const [query, setquery] = useState("");
    const [bookList, setbookList] = useState([]);
    const [openSearchResult, setOpenSearchResult] = useState();

    const searchBook = async () => {
        try {
            const res = await bookService.searchBook(query);
            setbookList(res);
            console.log(res) // to test gloabal search api 
        } catch (err) {
            alert('Error occured while searching.  Check the API and try again. Error : ', err)
        }
    };

    const search = () => {
        document.body.classList.add("search-result-open");
        searchBook();
        setOpenSearchResult(true)
    };

    return(
        <>

        {/* st temporary */}

        {/* <div className="h-navigation-temp">
                <ul className="h-ul">
                    <li><Link to="/home"><Button>Home</Button></Link></li>
                    <li><Link to='/product'><Button> Product </Button></Link></li>
                    <li><Link to='/login'><Button> Login </Button></Link></li>
                    <li><Link to='/register'><Button> Register </Button></Link></li>
                </ul>
        </div> */}

        {/* nd temporary */}
        
        <div className="h-container">
            <div className="h-container-wrapper">
                <div className="h-container-lft">
                    <Link to='/home'>
                        <img 
                            src={addIcon} 
                            className="h-tt-logo" 
                            alt="TatvaSoft logo" 
                        />
                    </Link>
                </div>

                <div className="h-container-rght">
                    
                    <div className="h-log-reg">
                        <ButtonGroup 
                            variant="text" 
                            aria-label="text button group"
                        >
                            <Button
                                className="h-br-1"
                                size="small"
                                sx={{ 
                                    color: "#f14d54",
                                    borderColor: "#f14d54 !important"
                                }}
                                onClick={handleLogin}
                            > 
                                Login 
                            </Button>
                                                   
                            <Button
                                size="small"
                                sx={{ 
                                    color: "#f14d54" 
                                }}
                                onClick={handleRegister}
                            > 
                                Register 
                            </Button>
                        </ButtonGroup>
                    </div>

                    <div className="h-rght-cart">
                        <Link to='/cart'>
                            <Button 
                                size="small"
                                variant="outlined" 
                                sx={{
                                    color: '#f14d54',
                                    borderColor: '#f14d54'
                                }}
                                startIcon={<ShoppingCartIcon />}
                            >
                                {0} Cart 
                            </Button>
                        </Link>
                    </div>
                </div>
            </div>    
        </div>
        
        <div 
            className="search-overlay"
            onClick={ () => {
                document.body.classList.remove('search-result-open')
                setOpenSearchResult(false)
            }}
        >
            {/* This is an Empty div     */}
        </div>

        <div className="h-searchbar">
            <div className="h-searchbar-center">
                {/* <Autocomplete
                    className="h-autocomplete"
                    disablePortal
                    id="combo-box-demo"
                    options={topBooks}
                    sx={{ width: 300 }}
                    renderInput={(params) => <TextField {...params} placeholder="What are you looking for..."/>}
                /> */}
                <div className="h-txtfield">
                    <TextField 
                        id="outlined-basic" 
                        placeholder='what are you looking for...' 
                        variant="outlined" 

                        name="text"
                        value={query}
                        onChange={(e) => setquery(e.target.value)}
                    />
                    {openSearchResult && 
                    <>
                        <div className="h-product-list">
                            <div className="h-no-prod">
                                {bookList?.length === 0 && (
                                        <p className="h-not-found"> No Products Found </p>
                                    )
                                }
                            </div>
                            <List className="h-related-product-list">
                                {bookList?.length > 0 && bookList.map((item, i) => {
                                    return(
                                        <ListItem>
                                            <div className="h-product-list-inner">
                                                <div className="h-inner-lft">
                                                    <span className="txt-41 txt-lb">
                                                        {item.name}
                                                    </span>
                                                    <p>
                                                        {item.description}
                                                    </p>
                                                </div>
                                                <div className="h-inner-rght">
                                                    <span>
                                                        {item.price}
                                                    </span>
                                                    <Button
                                                        size="small"
                                                        className="c-f14d54"
                                                    >
                                                        Add to Cart
                                                    </Button>
                                                </div>
                                            </div>
                                        </ListItem>
                                    )})    
                                }
                            </List>
                        </div>
                    </>
                    }
                </div>

                <div className="h-search-btn">
                    <Button 
                        type="submit"
                        variant="contained" 
                        className="bg-search" 
                        startIcon={<Search />}

                        onClick={search}
                    > 
                        Search 
                    </Button>
                </div>

                <div  className="h-search-btn">
                    <Button 
                        variant="contained" 
                        className="bg-f14d54"
                        onClick={handleCansal}
                    > 
                        Cancel 
                    </Button>
                </div>
            </div>
        </div>

        </>
    );
}

export default Header;
