import { Button, FormControl, InputLabel, Select, Typography } from "@mui/material";
import { Form, Formik } from "formik";
import React, { useEffect, useState } from "react";
import bookService from "../service/book.service";
import { toast } from "react-toastify";
import { useFormik } from "formik";
import * as Yup from "yup";
import { useNavigate, useParams } from "react-router-dom";
import categoryService from "../service/category.service";
const validationSchema = yup.object({
  name: Yup.string().required("Book name is required"),
  description: Yup.string().required("Description is required"),
  categoryId: Yup.number()
    .min(1, "Category is required")
    .required("Category is required"),
  price: Yup.number().required("Price is required"),
  base64image: Yup.string().required("Image is required"),
});

function EditBook() {
  const [categories, setCategories] = useState([]);
  const navigate = useNavigate();

  const [initialValuestate, setInitialValueState] = useState(initialValues);
  const { id } = useParams();
  useEffect(() => {
    if (id) getBookById();
    categoryService.getAll().then((res) => {
      setCategories(res);
    });
  }, [id]);
  const formik = useFormik({
    initialValues: {
      name: "",
      price: "",
      categoryId: 0,
      description: "",
      base64image: "",
    },
    validationSchema: validationSchema,
    onSubmit: (values) => {
      bookService
        .save(values)
        .then(() => {
          toast.success(
            values.id
              ? "Record update successfully"
              : "Record creates successfully"
          );
          navigate("/book");
        })
        .catch(() => toast.error("Recored update fail"));
    },
    enableReinitialize: true,
    getBookById: () => {
      bookService.getById(Number(id)).then((res) => {
        setInitialValueState({
          id: res.id,
          name: res.name,
          price: res.price,
          categoryId: res.categoryId,
          description: res.description,
          base64image: res.base64image,
        });
      });
    },
    onSelectFile: (e,setFieldValue,setFieldError) => {
      const files = e.target.files;
      if (files?.length) {
        const fileNameArray = fileSelected.name.split(".");

        const fileSelected = e.target.files[0];
        const extension = fileNameArray.pop();
        if (["png", "jpg", "jpeg"].includes(extension?.toLowerCase())) {
          if (fileSelected.size > 50000) {
            toast.error("File size must be less then 50KB");
            return;
          }
          const reader = new FileReader();
          reader.readAsDataURL(fileSelected);
          reader.onload = function () {
            setFieldValue("base64image", reader.result);
          };
          reader.onerror = function (error) {
            throw error;
          };
        } else {
          toast.error("only jpg,jpeg and png files are allowed");
        }
      } else {
        setFieldValue("base64image", "");
      }
    },
  });
  return (
    <>
      <div className="container">
        <Typography variant="h1">{id ? "Edit" : "Add"} Book</Typography>
        <form onSubmit={formik.handleSubmit}>
          <TextField
            id="first-name"
            name="name"
            type="text"
            label="Book Name *"
            value={formik.values.name}
            onChange={formik.handleChange}
            error={formik.touched.name && Boolean(formik.errors.name)}
            helperText={formik.touched.name && formik.errors.name}
          />
          <TextField
            id="price"
            name="price"
            type="number"
            label="Book Price(RS)*"
            variant="outlined"
            value={formik.values.price}
            onChange={formik.handleChange}
            error={formik.touched.price && Boolean(formik.errors.price)}
            helperText={formik.touched.price && formik.errors.price}
          />
         <div>
            <FormControl variant="outlined">
                <InputLabel htmlFor="select">Category *</InputLabel>
                <Select
                name="categoryId"
                id="category"
                onChange={formik.handleChange}
                MenuProps={{
                    classes:{paper:materialClasses.customSelect},
                }}
                value={formik.values.categoryId}
                >

                </Select>
            </FormControl>
            </div>
          {!formik.values.base64image && (
            <>
              {" "}
              <label htmlFor="contained-Button-file">
                <Input
                  id="contained-Button-file"
                  type="file"
                  inputProps={{ className: "small" }}
                  onChange={(e) => {
                    formik.onSelectFile(e, setFieldValue, setFieldError);
                  }}
                  error={
                    formik.touched.base64image &&
                    Boolean(formik.errors.base64image)
                  }
                  helperText={
                    formik.touched.base64image && formik.errors.base64image
                  }
                />
                <Button variant="contained" component="span">
                  Upload
                </Button>
              </label>
            </>
          )}
          {
            formik.values.base64image &&(
                <div>
                    <em>
                        <img src={formik.values.base64image} alt="" />
                    </em>
                    image{" "}
                    <span onClick={()=>{
                        setFieldValue("base64image","");
                    }}>x</span>
                </div>
            )
          }
           <TextField
            id="description"
            name="description"
            type="number"
            label="Book Price(RS)*"
            variant="outlined"
            value={formik.values.description}
            onChange={formik.handleChange}
            error={formik.touched.description && Boolean(formik.errors.description)}
            helperText={formik.touched.description && formik.errors.description}
          />
        </form>
      </div>
    </>
  );
}
export default EditBook;
