import React, { useState } from "react";
import useAuthContext from "../context/auth";
import { useNavigate } from "react-router-dom";
function User(){
    const authContext=useAuthContext();
    const [filters,setFilters]=useState(defaultFilter);
    const[userList,setUserList]=useState({
        pageIndex: 0,
        pageSize: 10,
        totalPages: 1,
        items: [],
        totalItems: 0,
    });
    const [open, setOpen] = useState(false);
    const [selectedId, setSelectedId] = useState(0);
    const navigate = useNavigate();

    useEffect(() => {
        const timer = setTimeout(() => {
          if (filters.keyword === "") delete filters.keyword;
          getAllUsers({ ...filters });
        }, 500);
        return () => clearTimeout(timer);
      }, [filters]);
      const getAllUsers = (filters) => {
        userService.getAllUsers(filters).then((res) => {
          if(res){
            setUserList(res);
          }
        });
      };
      const columns = [
        { id: "firstName", label: "First Name", minWidth: 100 },
        { id: "lastName", label: "Last Name", minWidth: 100 },
        { id: "email", label: "Email", minWidth: 170 },
        { id: "roleName", label: "Role", minWidth: 130 },
      ];
      const onConfirmDelete = () => {
        userService
          .deleteUser(selectedId)
          .then((res) => {
if(res){toast.success("User delete sucessfully");
            setOpen(false);
            setFilters({ ...filters, pageIndex: 1 });}
          })
          .catch((e) => toast.error("Fail to delete User"));
      };
    return(
        
       <div className="productWrapper">
        <div className="container">
          <h1 align="center" style={{ marginBottom: 25, marginTop: 50 }}>
            Book Listing
          </h1>
          <center>
            <hr
              style={{
                background: "red",
                color: "red",
                borderColor: "red",
                height: "4px",
                marginInline: "30px",
                width: "200px",
              }}
            />
          </center>
          <div className="products">
            <div className="btn-wrapper" style={{ marginInlineEnd: "auto" }}>
              <TextField
                id="text"
                name="text"
                placeholder="Search..."
                variant="outlined"
                inputProps={{ className: "small" }}
                onChange={(e) => {
                  setFilters({
                    ...filters,
                    keyword: e.target.value,
                    pageIndex: 1,
                  });
                }}
              />
              
            </div>
          

          
          </div>
        </div>
      </div>
    
    );
}
export default User;
